graphql.query:
mutation($username: String!, $password: String!, $remember: String!) {
  authenticateUser(params: {
    username: $username
    password: $password
    remember: $remember
  }) {
    accessToken
    refreshToken
    idToken
    expiresIn
    tokenType
  }
}

mutation Mutation {
  generateToken {
    token_type
    expires_in
    jwt
    scope
  }
}

query CurrentWeather($siteKey: String!) {
  site(siteKey: $siteKey) {
    currentWeather {
      id
      weatherCondition
      weatherIcon
      timezone
      dateTime
      sunrise
      sunset
      temperature
      feelsLikeTemperature
      conditions
    }
  }
}

query Battery($siteKey: String!) {
  site(siteKey: $siteKey) {
    battery {
      backupDays
      backupDaysLeft
      backUpReserveSocLevel
      backupTimeLeft {
        formatted {
          days
          hours
          minutes
        }
        totalMinutes
      }
      operationMode
      siteKey
      siteLevelCustomerStateOfChargePercentage
      socAndChargeCapacity {
        chargeCapacity
        comSerialNumber
        customerStateOfCharge
        deviceKey
        deviceSerialNumber
        parentSerialNumber
        productModelName
        stateOfChargePercentage
      }
    }
  }
}

query ServiceCodes($siteKey: String!) {
  site(siteKey: $siteKey) {
    serviceCodes {
      alrtKey
      prodMdlNm
      comDvcSn
      connDvcSn
      dvcTy
      alrtNm
      alrtStFl
      trgrdEps
      rslvdEps
    }
  }
}

query PerformanceReport($siteKey: String!) {
  site(siteKey: $siteKey) {
    performanceReport {
      subscriptionId
      siteKey
      reportTypeName
      reportDisplayName
      statusIndicator
      fileFormat
      scheduleDay
      nextRun
      nextPeriod {
        start
        end
      }
      expirationDate
      subscribers {
        email
        statusIndicator
      }
    }
  }
}

query($siteKey: String!) {
    site(siteKey: $siteKey) {
        commissioningDate
        hasLivedata
        siteType
        assignments(assignmentType: COMMISSION) {
            deviceType
            deviceKey
            deviceSerialNumber
            assignmentType
            assignmentEffectiveTimestamp
            Logger: devices(deviceType: "logger") {
                dvcKey
                dvcTy
                fwVerNo
                sfwVerNo
                comProto
                deviceStatus{
                    essStatus
                    pvStatus
                    netIntfRptCtnt{
                        currNetIntfEnum
                        prevNetIntfEnum
                        sta0Ssid
                        wifiIconBars
                        cellIconBars
                    }
                }
            }
            Meters: devices(deviceType:"meter", deviceStatus:true){
                dvcKey
                lastRcvdEps
                meterType{
                    type
                    level
                }
            }
        }
    }
}

query Alerts($siteKey: String!) {
  site(siteKey: $siteKey) {
    alerts {
      alertId
      alertStatus
      alertType
      eventTimestamp
      deviceKey
      deviceSerialNumber
      deviceType
    }
  }
}

query FetchPartyData($partyId: String!) {
    party(partyId: $partyId) {
        partyId
        displayName
        email
        phone
        sites {
            siteKey
            hasWifi
            hasLivedata
            siteName
            siteType
            address1
            city
            state
            postalCode
            systemSize
            currentWeather {
                sunrise
                sunset
                timezone
                dateTime
            }
            battery {
                operationMode
                socAndChargeCapacity {
                    stateOfChargePercentage
                }
            }
            assignments(assignmentType: COMMISSION) {
                deviceSerialNumber
                deviceType
                deviceKey
                assignmentEffectiveTimestamp
            }
        }
    }
}

query EnergyRange($interval: String!, $end: String!, $start: String!, $siteKey: String!) {
  energyRange(interval: $interval, end: $end, start: $start, siteKey: $siteKey) {
    siteKey
    startdate
    enddate
    interval
    energyDataSeries {
      production
      consumption
      storage
      grid
    }
    totalProduction
    totalConsumption
    totalGridImport
    totalGridExport
    netGridImportExport
    totalStorageCharged
    totalStorageDischarged
    netStorageChargedDischarged
    energyMixPercentage
    environmentalSavings {
      costSaving {
        value
        unit
      }
      co2 {
        value
        unit
      }
      tree {
        value
        unit
      }
      mile {
        value
        unit
      }
      gas {
        value
        unit
      }
    }
    weather {
      timezone
      tzoffset
      days {
        datetime
        datetimeEpoch
        maxTemperature
        minTemperature
        temperature
        maxFeelsLikeTemperature
        minFeelsLikeTemperature
        feelsLikeTemperature
        sunrise
        sunriseEpoch
        sunset
        sunsetEpoch
        moonphase
        conditions
        hours {
          datetime
          datetimeEpoch
          temperature
          feelsLikeTemperature
          conditions
        }
      }
    }
    tooltipProductionTotals {
      total
      dateString
    }
    tooltipConsumptionTotals {
      total
      dateString
    }
    tooltipGridTotals {
      total
      dateString
    }
    tooltipStorageTotals {
      total
      dateString
    }
  }
}

query PowerDataSeries($interval: String!, $end: String!, $start: String!, $siteKey: String!) {
  site(siteKey: $siteKey) {
    power(interval: $interval, end: $end, start: $start) {
      powerDataSeries {
        production
        consumption
        storage
        grid
      }
    }
  }
}

query Site($siteKey: String!) {
  currentPower(siteKey: $siteKey) {
    production
    consumption
    storage
    grid
    timestamp
  }
}

query Panels($date: String!, $siteKey: String!) {
  panels(date: $date, siteKey: $siteKey) {
    panels {
      alerts {
        alertId
        alertStatus
        alertType
        eventTimestamp
        deviceKey
        deviceSerialNumber
        deviceType
      }
      dailyEnergyProduction
      energyColorCode
      lastCommunicationTimestamp
      layout {
        azimuth
        orientation
        rotation
        xCoordinate
        yCoordinate
      }
      peakPowerProduction {
        timestamp
        value
      }
      serialNumber
      sevenDayAverage
    }
  }
}

query Query($params: getCustomerCasesInput!) {
  getCases(params: $params) {
    caseNumber
    subject
    description
    category
    status
    createdDate
    lastModifiedDate
    statusColor
  }
}

mutation AddCaseComment($params: addCaseCommentInput!) {
  addCaseComment(params: $params) {
    message
    status
  }
}
